import { Component } from 'react';

export default class MultipleOptionsBuilder extends Component {
  constructor(props) {
    super(props);

    this.state = {
      optionCounter: [1],
    }

    this.addOption = this.addOption.bind(this);
    this.removeOption = this.removeOption.bind(this);
  }

  addOption() {
    if (prevState.optionCounter.length === 6) return;

    this.setState(function (prevState) {
      let i = prevState.optionCounter[prevState.optionCounter.length - 1] + 1;

      prevState.optionCounter.push(i);
      console.log(prevState)
      return prevState;
    })
  }

  removeOption(key) {
    this.setState(function (prevState) {
      prevState.optionCounter.splice(key,1);
      return prevState;
    })
  }

  render() {
    return(
      <div>
        {this.state.optionCounter.map((value, index) => (
          <div key={index}>
            <input/>
            <button onClick={() => {this.removeOption(index)}}>Remove Option</button>
          </div>
        ))}
        <button onClick={this.addOption}>Add Option</button>
      </div>
    )
  }
}
