import {auth, firestore} from '../firebaseConfig'

const addUserToCollection = async(result) => {

    let oDocument = {

    }

    try{
        const document = await firestore.collection('users').add({
            uid: result.user.uid,
            email: result.user.email
        })

        oDocument.ok = true
        oDocument.data = document
    }
    catch(err){
        oDocument.ok = false
        oDocument.error = err
    }

    return oDocument
}

export const firebaseLogin = async(email, password) => {
    let result

    try{    
        result = await auth.signInWithEmailAndPassword(email, password)
    }
    catch(err){
        console.log(err)
    }

    if(result)
        return result.user
    else
        return null
}

export const firebaseSignUp = async(email, password) => {
    let result

    try{
        result = await auth.createUserWithEmailAndPassword(email, password)
        const oDocument = await addUserToCollection(result)

        //if there's a problem saving user in users collection, then it is deleted
        if(!oDocument.ok){
            await auth.currentUser.delete()
            throw {...oDocument.error}
        }
    }
    catch(err){
        result = err
    }

    return result
}
